mapper.select:
SELECT *, week(measureDate) as week 
from Weight where petId=#{petId} and week(measureDate) = #{week}
order by measureDate

select a.measureDate, a.week, b.avg from 
(
SELECT measureDate, week(measureDate) as week
FROM Weight 
where petId=#{petId}
) as a
inner join (
SELECT week(measureDate) as week, round(avg(kg),2) as avg
FROM Weight 
where petId=#{petId}
group by week
) as b
on a.week = b.week
order by measureDate;

mapper.update:
update Weight
set measureDate=#{measureDate},
	measureTime=#{measureTime},
	kg=#{kg}
where id=#{id}
